onst_itrator(tail);<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; }<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; int size() const {<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; return theSize;<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; }<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; bool empty() const {<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; return size() == 0;<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; }<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; void clear() {<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; while(!empty()) {<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; pop_front();<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; }<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; }<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Object &amp; front() {<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; return *begin();<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; }<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; const Object &amp; front() const {<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; return *begin();<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; }<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Object &amp; back() {<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; return *--end();<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; }<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; const Object &amp; back() const {<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; return *--end();<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; }<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; void push_front(const Object &amp; x) {<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; insert(begin(), x);<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; }<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; void push_back(const Object &amp; x) {<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; insert(end(), x);<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; }<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; void pop_front() {<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; erase(begin());<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; }<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; void pop_back() {<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; erase(--end());<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; }<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; iterator insert(iterator itr, const Object &amp; x) {<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Node * p = itr.current;<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; theSize++;<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; return iterator(p-&gt;prev = p-&gt;prev-&gt;next = new Node(x, p-&gt;prev, p));<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; }<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; iterator erase(iterator itr) {<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Node * p = itr.current;<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; iterator retVal(p-&gt;next);<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; p-&gt;prev-&gt;next = p-&gt;next;<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; p-&gt;next-&gt;prev = p-&gt;prev;<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; delete p;<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; theSize--;<br />\r\n<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; return retVal;<br />\r\n<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; }<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; iterator erase(iterator start, iterator end) {<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; for(iterator itr = start; itr != end; )<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; itr = erase(itr);<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; return end;<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; }<br />\r\n&nbsp;&nbsp;&nbsp; private:<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; int theSize;<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Node * head; //表头节点，不存放数据，只作为标识<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Node * tail; //表尾节点，不存放数据，只作为标识<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; void init() {<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; theSize = 0;<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; head = new Node;<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; tail = new Node;<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; head-&gt;next = tail;<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; tail-&gt;prev = head;<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; }<br />\r\n<p>\r\n	};\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	list.cc\r\n</p>\r\n<p>\r\n	using namespace std;<br />\r\n#include&lt;iostream&gt;<br />\r\n#include "list.h"<br />\r\n<br />\r\n<br />\r\n/*<br />\r\nint main() {<br />\r\n&nbsp;&nbsp;&nbsp; int i;<br />\r\n&nbsp;&nbsp;&nbsp; cin &gt;&gt; i;<br />\r\n&nbsp;&nbsp;&nbsp; cout &lt;&lt; i &lt;&lt; endl;<br />\r\n&nbsp;&nbsp;&nbsp; List&lt;int&gt; mylist;<br />\r\n&nbsp;&nbsp;&nbsp; cout &lt;&lt; mylist.size();<br />\r\n&nbsp;&nbsp;&nbsp; mylist.push_front(i);<br />\r\n&nbsp;&nbsp;&nbsp; cout &lt;&lt; mylist.back() &lt;&lt; endl;<br />\r\n&nbsp;&nbsp;&nbsp; cout &lt;&lt; mylist.size();<br />\r\n&nbsp;&nbsp;&nbsp; return 0;<br />\r\n<br />\r\n}<br />\r\n*/<br />\r\nint main (){<br />\r\n&nbsp;&nbsp;&nbsp; List&lt;int&gt; mylist;<br />\r\n&nbsp;&nbsp;&nbsp; List&lt;int&gt;::iterator it1, it2;<br />\r\n&nbsp;&nbsp;&nbsp; cout &lt;&lt; mylist.size();<br />\r\n&nbsp;&nbsp;&nbsp; // set some values:<br />\r\n&nbsp;&nbsp;&nbsp; for (int i=1; i&lt;10; ++i) <br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; mylist.push_back(i*10); // 10 20 30 40 50 60 70 80 90<br />\r\n&nbsp;&nbsp;&nbsp; cout &lt;&lt; "mylist contains:";<br />\r\n&nbsp;&nbsp;&nbsp; for (it1=mylist.begin(); it1!=mylist.end(); ++it1)<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; cout &lt;&lt; '' '' &lt;&lt; *it1;<br />\r\n&nbsp;&nbsp;&nbsp; cout &lt;&lt; ''\\n'';<br />\r\n&nbsp;&nbsp;&nbsp; it1 = it2 = mylist.begin(); // ^^<br />\r\n&nbsp;&nbsp;&nbsp; for (int i = 0; i &lt; 6; i++) {<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; it2++;<br />\r\n&nbsp;&nbsp;&nbsp; }<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; // ^&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; ^<br />\r\n&nbsp;&nbsp;&nbsp; ++it1;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; //&nbsp;&nbsp;&nbsp; ^&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; ^<br />\r\n&nbsp;&nbsp;&nbsp; it1 = mylist.erase (it1);&nbsp;&nbsp; // 10 30 40 50 60 70 80 90<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; //&nbsp;&nbsp;&nbsp; ^&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; ^<br />\r\n<br />\r\n&nbsp;&nbsp;&nbsp; it2 = mylist.erase (it2);&nbsp;&nbsp; // 10 30 40 50 60 80 90<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; //&nbsp;&nbsp;&nbsp; ^&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; ^<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; ++it1;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; //&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; ^&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; ^<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; //--it2;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; //&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; ^&nbsp;&nbsp;&nbsp;&nbsp; ^<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; mylist.erase (it1,it2);&nbsp;&nbsp;&nbsp;&nbsp; // 10 30 60 80 90<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; //&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; ^<br />\r\n&nbsp;&nbsp;&nbsp; cout &lt;&lt; "mylist contains:";<br />\r\n&nbsp;&nbsp;&nbsp; for (it1=mylist.begin(); it1!=mylist.end(); ++it1)<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; cout &lt;&lt; '' '' &lt;&lt; *it1;<br />\r\n&nbsp;&nbsp;&nbsp; cout &lt;&lt; ''\\n'';<br />\r\n&nbsp;&nbsp;&nbsp; return 0;<br />\r\n}\r\n</p>', 1385194834, 1385194834, 30, '未知', 50, 'C++ list', 1, 1, 1);
INSERT INTO `ablg_article` (`id`, `property`, `image`, `cid`, `keywords`, `description`, `content`, `createtime`, `updatetime`, `click`, `source`, `sort`, `title`, `is_check`, `adminid`, `status`) VALUES
(23, '', '', 1, '', ' 产生段错误就是访问了错误的内存段，一般是你没有权限，或者根本就不存在对应的物理内存，尤其常见的是访问0地址。 一般来说，段错误就是指访问的内存超出了系统所给这个程序的内存空间，通常这个值是由gdtr来保存的，它是一个48位的寄存器，其中的32位是保存由它指向的gdt表，后13位保存相应于gdt的下标，最后3位包括了程序是否在内存中以及程序在cpu中的运行级别，指向的gdt', '<div style="margin-left:0px;background-color:#C7CBBD;color:#494949;font-family:Arial, Helvetica, sans-serif;">\r\n	<div style="margin-left:0px;background-color:transparent;">\r\n		<div style="margin-left:0px;background-color:transparent;">\r\n			<div style="margin-left:0px;background-color:transparent;">\r\n				<div style="margin-left:0px;background-color:transparent;" align="left">\r\n					<div style="margin-left:0px;background-color:transparent;">\r\n						<p style="margin-left:auto;">\r\n							产生段错误就是访问了错误的内存段，一般是你没有权限，或者根本就不存在对应的物理内存，尤其常见的是访问0地址。\r\n						</p>\r\n						<p style="margin-left:auto;">\r\n							一般来说，段错误就是指访问的内存超出了系统所给这个程序的内存空间，通常这个值是由gdtr来保存的，它是一个48位的寄存器，其中的32位是保存由它\r\n指向的gdt表，后13位保存相应于gdt的下标，最后3位包括了程序是否在内存中以及程序在cpu中的运行级别，指向的gdt是以64位为一个单位的\r\n表，在这张表中就保存着程序运行的代码段、数据段的起始地址、与此相应的段限和页面交换、程序运行级别还有内存粒度等等的信息。一旦一个程序发生了越界访\r\n问，cpu就会产生相应的异常保护，于是segmentation\r\nfault就出现了.<br />\r\n<br />\r\n在编程中以下几类做法容易导致段错误，基本是是错误地使用指针引起的：<br />\r\n<br />\r\n1)访问系统数据区，尤其是往系统保护的内存地址写数据，最常见的就是给一个指针以0地址；<br />\r\n2)内存越界(数组越界，变量类型不一致等) 访问到不属于你的内存区域。<br />\r\n<br />\r\n解决方法<br />\r\n<br />\r\n我们在用C/C++语言写程序的时侯，内存管理的绝大部分工作都是需要我们来做的。实际上，内存管理是一个比较繁琐的工作，无论你多高明，经验多丰富，\r\n难免会在此处犯些小错误，而通常这些错误又是那么的浅显而易于消除。但是手工“除虫”（debug），往往是效率低下且让人厌烦的，本文将就“段错误”这个\r\n内存访问越界的错误谈谈如何快速定位这些“段错误”的语句。<br />\r\n下面将就以下的一个存在段错误的程序介绍几种调试方法：\r\n						</p>\r\n					</div>\r\n					<div style="margin-left:0px;background-color:transparent;">\r\n						<table style="border-collapse:collapse;width:551px;font-size:12px;" align="center">\r\n							<tbody>\r\n								<tr>\r\n									<td>\r\n										1 dummy_function (void)<br />\r\n2 {<br />\r\n3&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;unsigned char *ptr = 0x00;<br />\r\n4&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;*ptr = 0x00;<br />\r\n5 }<br />\r\n6<br />\r\n7 int main (void)<br />\r\n8 {<br />\r\n9&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;dummy_function ();<br />\r\n10<br />\r\n11&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;return 0;<br />\r\n12 }\r\n									</td>\r\n								</tr>\r\n							</tbody>\r\n						</table>\r\n					</div>\r\n					<div style="margin-left:0px;background-color:transparent;">\r\n						　　作为一个熟练的C/C++程序员，以上代码的bug应该是很清楚的，因为它尝试操作地址为0的内存区域，而这个内存区域通常是不可访问的禁区，当然就会出错了。我们尝试编译运行它:\r\n					</div>\r\n					<div style="margin-left:0px;background-color:transparent;">\r\n						<table style="border-collapse:collapse;width:551px;font-size:12px;" align="center">\r\n							<tbody>\r\n								<tr>\r\n									<td>\r\n										xiaosuo@gentux test $ ./a.out<br />\r\n段错误\r\n									</td>\r\n								</tr>\r\n							</tbody>\r\n						</table>\r\n					</div>\r\n					<div style="margin-left:0px;background-color:transparent;">\r\n						　　果然不出所料，它出错并退出了。\r\n					</div>\r\n					<div style="margin-left:0px;background-color:transparent;">\r\n						<span style="font-weight:bold;"> 　　1.利用gdb逐步查找段错误:</span>\r\n					</div>\r\n					<div style="margin-left:0px;background-color:transparent;">\r\n						<br />\r\n这种方法也是被大众所熟知并广泛采用的方法，首先我们需要一个带有调试信息的可执行程序，所以我们加上“-g\r\n-rdynamic"的参数进行编译，然后用gdb调试运行这个新编译的程序,具体步骤如下:\r\n					</div>\r\n					<div style="margin-left:0px;background-color:transparent;">\r\n						<table style="border-collapse:collapse;width:551px;font-size:12px;" align="center">\r\n							<tbody>\r\n								<tr>\r\n									<td>\r\n										xiaosuo@gentux test $ gcc -g -rdynamic d.c<br />\r\nxiaosuo@gentux test $ gdb ./a.out<br />\r\nGNU gdb 6.5<br />\r\nCopyright (C) 2006 Free Software Foundation, Inc.<br />\r\nGDB is free software, covered by the GNU General Public License,\r\nand you are<br />\r\nwelcome to change it and/or distribute copies of it under certain\r\nconditions.<br />\r\nType "show copying" to see the conditions.<br />\r\nThere is absolutely no warranty for GDB. Type "show warranty" for\r\ndetails.<br />\r\nThis GDB was configured as "i686-pc-linux-gnu"...Using host\r\nlibthread_db library "/lib/libthread_db.so.1".<br />\r\n<br />\r\n(gdb) r<br />\r\nStarting program: /home/xiaosuo/test/a.out<br />\r\n<br />\r\nProgram received signal SIGSEGV, Segmentation fault.<br />\r\n0x08048524 in dummy_function () at d.c:4<br />\r\n4 *ptr = 0x00;<br />\r\n(gdb)\r\n									</td>\r\n								</tr>\r\n							</tbody>\r\n						</table>\r\n					</div>\r\n					<div style="margin-left:0px;background-color:transparent;">\r\n						　　好像不用一步步调试我们就找到了出错位置d.c文件的第4行，其实就是如此的简单。\r\n					</div>\r\n					<div style="margin-left:0px;background-color:transparent;">\r\n						<br />\r\n从这里我们还发现进程是由于收到了SIGSEGV信号而结束的。通过进一步的查阅文档(man 7\r\nsignal)，我们知道SIGSEGV默认的处理动作是打印“段错误”的出错信息，并产生Core文件，由此我们又产生了方法二。\r\n					</div>\r\n					<div style="margin-left:0px;background-color:transparent;">\r\n						<span style="font-weight:bold;"> 　　2.分析Core文件：</span>\r\n					</div>\r\n					<div style="margin-left:0px;background-color:transparent;">\r\n						<br />\r\nCore文件是什么呢？\r\n					</div>\r\n					<div style="margin-left:0px;background-color:transparent;">\r\n						<table style="border-collapse:collapse;width:551px;font-size:12px;" align="center">\r\n							<tbody>\r\n								<tr>\r\n									<td>\r\n										The default action of certain signals is to cause a process to\r\nterminate and produce a core dump file, a disk file containing an\r\nimage of the process''s memory at the time of termination. A list of\r\nthe signals which cause a process to dump core can be found in\r\nsignal(7).\r\n									</td>\r\n								</tr>\r\n							</tbody>\r\n						</table>\r\n					</div>\r\n					<div style="margin-left:0px;background-color:transparent;">\r\n						　　以上资料摘自man page(man 5\r\ncore)。不过奇怪了，我的系统上并没有找到core文件。后来，忆起为了减少系统上的拉圾文件的数量（本人有些洁癖，这也是我喜欢Gentoo的原因之一），禁止了core文件的生成，查看了以下果真如此，将系统的core文件的大小限制在512K大小，再试：\r\n					</div>\r\n					<div style="margin-left:0px;background-color:transparent;">\r\n						<table style="border-collapse:collapse;width:551px;font-size:12px;" align="center">\r\n							<tbody>\r\n								<tr>\r\n									<td>\r\n										xiaosuo@gentux test $ ulimit -c<br />\r\n0<br />\r\nxiaosuo@gentux test $ ulimit -c 1000<br />\r\nxiaosuo@gentux test $ ulimit -c<br />\r\n1000<br />\r\nxiaosuo@gentux test $ ./a.out<br />\r\n段错误 (core dumped)<br />\r\nxiaosuo@gentux test $ ls<br />\r\na.out core d.c f.c g.c pango.c test_iconv.c test_regex.c\r\n									</td>\r\n								</tr>\r\n							</tbody>\r\n						</table>\r\n					</div>\r\n					<div style="margin-left:0px;background-color:transparent;">\r\n						　　core文件终于产生了，用gdb调试一下看看吧:\r\n					</div>\r\n					<div style="margin-left:0px;background-color:transparent;">\r\n						<table style="border-collapse:collapse;width:551px;font-size:12px;" align="center">\r\n							<tbody>\r\n								<tr>\r\n									<td>\r\n										xiaosuo@gentux test $ gdb ./a.out core<br />\r\nGNU gdb 6.5<br />\r\nCopyright (C) 2006 Free Software Foundation, Inc.<br />\r\nGDB is free software, covered by the GNU General Public License,\r\nand you are<br />\r\nwelcome to change it and/or distribute copies of it under certain\r\nconditions.<br />\r\nType "show copying" to see the conditions.<br />\r\nThere is absolutely no warranty for GDB. Type "show warranty" for\r\ndetails.<br />\r\nThis GDB was configured as "i686-pc-linux-gnu"...Using host\r\nlibthread_db library "/lib/libthread_db.so.1".<br />\r\n<br />\r\n<br />\r\nwarning: Can''t read pathname for load map: 输入/输出错误.<br />\r\nReading symbols from /lib/libc.so.6...done.<br />\r\nLoaded symbols for /lib/libc.so.6<br />\r\nReading symbols from /lib/ld-linux.so.2...done.<br />\r\nLoaded symbols for /lib/ld-linux.so.2<br />\r\nCore was generated by `./a.out''.<br />\r\nProgram terminated with signal 11, Segmentation fault.<br />\r\n#0 0x08048524 in dummy_function () at d.c:4<br />\r\n4 *ptr = 0x00;\r\n									</td>\r\n								</tr>\r\n							</tbody>\r\n						</table>\r\n					</div>\r\n					<div style="margin-left:0px;background-color:transparent;">\r\n						　　还是一步就定位到了错误所在地，佩服一下Linux/Unix系统的此类设计。<br />\r\n接着考虑下去，以前用windows系统下的ie的时侯，有时打开某些网页，会出现“运行时错误”，这个时侯如果恰好你的机器上又装有windows的编译器的话，他会弹出来一个对话框，问你是否进行调试，如果你选择是，编译器将被打开，并进入调试状态，开始调试。\r\n					</div>\r\n					<div style="margin-left:0px;background-color:transparent;">\r\n						　　Linux下如何做到这些呢？我的大脑飞速地旋转着，有了，让它在SIGSEGV的handler中调用gdb，于是第三个方法又诞生了：\r\n					</div>\r\n					<div style="margin-left:0px;background-color:transparent;">\r\n						<br />\r\n<span style="font-weight:bold;"> 　　3.段错误时启动调试:</span>\r\n					</div>\r\n					<div style="margin-left:0px;background-color:transparent;">\r\n						<table style="border-collapse:collapse;width:551px;font-size:12px;" align="center">\r\n							<tbody>\r\n								<tr>\r\n									<td>\r\n										#include&nbsp;<br />\r\n#include&nbsp;<br />\r\n#include&nbsp;<br />\r\n#include&nbsp;<br />\r\n<br />\r\nvoid dump(int signo)<br />\r\n{<br />\r\nchar buf[1024];<br />\r\nchar cmd[1024];<br />\r\nFILE *fh;<br />\r\n<br />\r\nsnprintf(buf, sizeof(buf), "/proc/%d/cmdline", getpid());<br />\r\nif(!(fh = fopen(buf, "r")))<br />\r\nexit(0);<br />\r\nif(!fgets(buf, sizeof(buf), fh))<br />\r\nexit(0);<br />\r\nfclose(fh);<br />\r\nif(buf[strlen(buf) - 1] == ''n'')<br />\r\nbuf[strlen(buf) - 1] = '''';<br />\r\nsnprintf(cmd, sizeof(cmd), "gdb %s %d", buf, getpid());<br />\r\nsystem(cmd);<br />\r\n<br />\r\nexit(0);<br />\r\n}<br />\r\n<br />\r\nvoid<br />\r\ndummy_function (void)<br />\r\n{<br />\r\nunsigned char *ptr = 0x00;<br />\r\n*ptr = 0x00;<br />\r\n}<br />\r\n<br />\r\nint<br />\r\nmain (void)<br />\r\n{<br />\r\nsignal(SIGSEGV, &amp;dump);<br />\r\ndummy_function ();<br />\r\n<br />\r\nreturn 0;<br />\r\n}\r\n									</td>\r\n								</tr>\r\n							</tbody>\r\n						</table>\r\n					</div>\r\n					<div style="margin-left:0px;background-color:transparent;">\r\n						　　编译运行效果如下：\r\n					</div>\r\n					<div style="margin-left:0px;background-color:transparent;">\r\n						<table style="border-collapse:collapse;width:551px;font-size:12px;" align="center">\r\n							<tbody>\r\n								<tr>\r\n									<td>\r\n										xiaosuo@gentux test $ gcc -g -rdynamic f.c<br />\r\nxiaosuo@gentux test $ ./a.out<br />\r\nGNU gdb 6.5<br />\r\nCopyright (C) 2006 Free Software Foundation, Inc.<br />\r\nGDB is free software, covered by the GNU General Public License,\r\nand you are<br />\r\nwelcome to change it and/or distribute copies of it under certain\r\nconditions.<br />\r\nType "show copying" to see the conditions.<br />\r\nThere is absolutely no warranty for GDB. Type "show warranty" for\r\ndetails.<br />\r\nThis GDB was configured as "i686-pc-linux-gnu"...Using host\r\nlibthread_db library "/lib/libthread_db.so.1".<br />\r\n<br />\r\nAttaching to program: /home/xiaosuo/test/a.out, process\r\n9563<br />\r\nReading symbols from /lib/libc.so.6...done.<br />\r\nLoaded symbols for /lib/libc.so.6<br />\r\nReading symbols from /lib/ld-linux.so.2...done.<br />\r\nLoaded symbols for /lib/ld-linux.so.2<br />\r\n0xffffe410 in __kernel_vsyscall ()<br />\r\n(gdb) bt<br />\r\n#0 0xffffe410 in __kernel_vsyscall ()<br />\r\n#1 0xb7ee4b53 in waitpid () from /lib/libc.so.6<br />\r\n#2 0xb7e925c9 in strtold_l () from /lib/libc.so.6<br />\r\n#3 0x08048830 in dump (signo=11) at f.c:22<br />\r\n#4&nbsp;<br />\r\n#5 0x0804884c in dummy_function () at f.c:31<br />\r\n#6 0x08048886 in main () at f.c:38\r\n									</td>\r\n								</tr>\r\n							</tbody>\r\n						</table>\r\n					</div>\r\n					<div style="margin-left:0px;background-color:transparent;">\r\n						　　怎么样？是不是依旧很酷？\r\n					</div>\r\n					<div style="margin-left:0px;background-color:transparent;">\r\n						　　以上方法都是在系统上有gdb的前提下进行的，如果没有呢？其实glibc为我们提供了此类能够dump栈内容的函数簇，详见\r\n/usr/include/execinfo.h（这些函数都没有提供man\r\npage，难怪我们找不到），另外你也可以通过gnu的手册进行学习。\r\n					</div>\r\n					<div style="margin-left:0px;background-color:transparent;">\r\n						<span style="font-weight:bold;"> 　　4.利用backtrace和objdump进行分析：</span>\r\n					</div>\r\n					<div style="margin-left:0px;background-color:transparent;">\r\n						　　重写的代码如下:\r\n					</div>\r\n					<div style="margin-left:0px;background-color:transparent;">\r\n						<table style="border-collapse:collapse;width:551px;font-size:12px;" align="center">\r\n							<tbody>\r\n								<tr>\r\n									<td>\r\n										#include&nbsp;<br />\r\n#include&nbsp;<br />\r\n#include&nbsp;<br />\r\n#include&nbsp;<br />\r\n<br />\r\n<br />\r\nvoid<br />\r\ndummy_function (void)<br />\r\n{<br />\r\nunsigned char *ptr = 0x00;<br />\r\n*ptr = 0x00;<br />\r\n}<br />\r\n<br />\r\nvoid dump(int signo)<br />\r\n{<br />\r\nvoid *array[10];<br />\r\nsize_t size;<br />\r\nchar **strings;<br />\r\nsize_t i;<br />\r\n<br />\r\nsize = backtrace (array, 10);<br />\r\nstrings = backtrace_symbols (array, size);<br />\r\n<br />\r\nprintf ("Obtained %zd stack frames.n", size);<br />\r\n<br />\r\nfor (i = 0; i &lt; size; i++)<br />\r\nprintf ("%sn", strings[i]);<br />\r\n<br />\r\nfree (strings);<br />\r\n<br />\r\nexit(0);<br />\r\n}<br />\r\n<br />\r\nint<br />\r\nmain (void)<br />\r\n{<br />\r\nsignal(SIGSEGV, &amp;dump);<br />\r\ndummy_function ();<br />\r\n<br />\r\nreturn 0;<br />\r\n}\r\n									</td>\r\n								</tr>\r\n							</tbody>\r\n						</table>\r\n					</div>\r\n					<div style="margin-left:0px;background-color:transparent;">\r\n						　　编译运行结果如下：\r\n					</div>\r\n					<div style="margin-left:0px;background-color:transparent;">\r\n						<table style="border-collapse:collapse;width:551px;font-size:12px;" align="center">\r\n							<tbody>\r\n								<tr>\r\n									<td>\r\n										xiaosuo@gentux test $ gcc -g -rdynamic g.c<br />\r\nxiaosuo@gentux test $ ./a.out<br />\r\nObtained 5 stack frames.<br />\r\n./a.out(dump+0x19) [0x80486c2]<br />\r\n[0xffffe420]<br />\r\n./a.out(main+0x35) [0x804876f]<br />\r\n/lib/libc.so.6(__libc_start_main+0xe6) [0xb7e02866]<br />\r\n./a.out [0x8048601]\r\n									</td>\r\n								</tr>\r\n							</tbody>\r\n						</table>\r\n					</div>\r\n					<div style="margin-left:0px;background-color:transparent;">\r\n						　　这次你可能有些失望,似乎没能给出足够的信息来标示错误,不急,先看看能分析出来什么吧,用objdump反汇编程序,找到地址0x804876f对应的代码位置:\r\n					</div>\r\n					<div style="margin-left:0px;background-color:transparent;">\r\n						<table style="border-collapse:collapse;width:551px;font-size:12px;" align="center">\r\n							<tbody>\r\n								<tr>\r\n									<td>\r\n										xiaosuo@gentux test $ objdump -d a.out\r\n									</td>\r\n								</tr>\r\n							</tbody>\r\n						</table>\r\n					</div>\r\n					<div style="margin-left:0px;background-color:transparent;">\r\n						<table style="border-collapse:collapse;width:551px;font-size:12px;" align="center">\r\n							<tbody>\r\n								<tr>\r\n									<td>\r\n										8048765: e8 02 fe ff ff call 804856c\r\n									</td>\r\n								</tr>\r\n							</tbody>\r\n						</table>\r\n					</div>\r\n					<div style="margin-left:0px;background-color:transparent;">\r\n						　　我们还是找到了在哪个函数(dummy_function)中出错的,信息已然不是很完整,不过有总比没有好的啊!\r\n					</div>\r\n					<div style="margin-left:0px;background-color:transparent;">\r\n						<span style="font-weight:bold;"> 　　后记：</span>\r\n					</div>\r\n					<div style="margin-left:0px;background-color:transparent;">\r\n						<span style="font-weight:bold;"> 　　</span>本文给出了分析"段错误"的几种方法,不要认为这是与孔乙己先生的"回"字四种写法一样的哦,因为每种方法都有其自身的适用范围和适用环境,请酌情使用,或遵医嘱\r\n					</div>\r\n				</div>\r\n			</div>\r\n		</div>\r\n	</div>\r\n</div>', 1385195037, 1385195037, 63, '未知', 50, 'Linux(C++ 断错误)系统内存错误产生的原因及调试方法(段错误|core dumped)', 1, 1, 1),
(24, 'h,c', '', 5, '', ' 这两个帮助生成伪随机数的函数包含在标准库 中，所以首先需在头文件中包含进来。 注意：在C语言中包含在标准库中。 函数一：intrand(void)； 从srand (seed)中指定的seed开始，返回一个[seed, RAND_MAX（0x7fff）)间的随机整数。 函数二：void srand(unsignedseed)； 参数seed是rand()的种子，用来初始化rand()的起始值。 用法：它需要提供一个种子，这个种子会对应一个随机数，如果使用相同的种子后面', '<span style="color:#003300;">这两个帮助生成伪随机数的函数包含在标准库 中，所以首先需在头文件中包含进来。</span> \r\n<p style="font-size:13px;">\r\n	<span style="color:#003300;">&nbsp;&nbsp;&nbsp;<span style="color:#FF0000;">注意：在C语言中包含在标准库中。</span></span><br />\r\n<br />\r\n<span style="color:#003300;">&nbsp;&nbsp;<span style="color:#000080;">&nbsp;函数一：int\r\n&nbsp;rand(void)；&nbsp;</span></span><br />\r\n<span style="color:#000080;">&nbsp;&nbsp;从srand &nbsp;(seed)中指定的seed开始，返回一个[seed, &nbsp;RAND_MAX（0x7fff）)间的随机整数。&nbsp;</span>\r\n</p>\r\n<p style="font-size:13px;">\r\n	<br />\r\n<span style="color:#000080;">&nbsp;&nbsp;函数二：void &nbsp;srand(unsigned\r\n&nbsp;seed)；&nbsp;</span><br />\r\n<span style="color:#000080;">&nbsp;&nbsp;参数seed是rand()的种子，用来初始化rand()的起始值。&nbsp;</span>\r\n</p>\r\n<p style="font-size:13px;">\r\n	<span style="color:#003300;"><span style="color:#000080;">&nbsp;&nbsp;&nbsp;用法：它需要提供一个种子，这个种子会对应一个随机数，如果使用相同的种子后面的rand()函数会出现一样的随机数。如：\r\nsrand(1); 直接使用1来初始化种子。不过为了防止随机数每次重复常常使用系统时间来初始化，即使用\r\ntime函数来获得系统时间，它的返回值为从 00:00:00 GMT, January 1, 1970\r\n到现在所持续的秒数，然后将time_t型数据转化为(unsigned)型再传给srand函数，即： srand((unsigned)\r\ntime(&amp;t)); 还有一个经常用法，不需要定义time_t型t变量,即： srand((unsigned)\r\ntime(NU</span>LL));\r\n直接传入一个空指针，因为你的程序中往往并不需要经过参数获得的t数据。srand((int)getpid());\r\n使用程序的ID(getpid())来作为初始化种子，在同一个程序中这个种子是固定的。</span><br />\r\n<br />\r\n<span style="color:#003300;">&nbsp;&nbsp;可以认为rand()在每次被调用的时候，它会查看：&nbsp;</span><br />\r\n<span style="color:#003300;">&nbsp;&nbsp;1）\r\n如果用户在此之前调用过srand(seed)，给seed指定了一个值，那么它会自动调用&nbsp;srand(seed)一次来初始化它的起始值。&nbsp;</span><br />\r\n<span style="color:#003300;">&nbsp;&nbsp;2）\r\n如果用户在此之前没有调用过srand(seed)，它会自动调用srand(1)一次。&nbsp;</span><br />\r\n<br />\r\n<span style="color:#003300;">&nbsp;&nbsp;根据上面的第一点我们可以得出：&nbsp;</span><br />\r\n<span style="color:#003300;">&nbsp;&nbsp;1）\r\n如果希望rand（）在每次程序运行时产生的值都不一样，必须给srand(seed)中的seed一个变值，这个变值必须在每次程序运行时都不一样（比如到目前为止流逝的时间）。&nbsp;</span><br />\r\n<span style="color:#003300;">&nbsp;&nbsp;2）\r\n否则，如果给seed指定的是一个定值，那么每次程序运行时rand（）产生的值都会一样，虽然这个值会是[seed,\r\n&nbsp;RAND_MAX（0x7fff）)之间的一个随机取得的值。&nbsp;</span><br />\r\n<span style="color:#003300;">&nbsp;&nbsp;3）\r\n如果在调用rand()之前没有调用过srand(seed)，效果将和调用了srand(1)再调用rand()一样（1也是一个定值）。&nbsp;</span><br />\r\n<br />\r\n<span style="color:#003300;">&nbsp;&nbsp;举几个例子，假设我们要取得0～6之间的随机整数（不含6本身）：&nbsp;</span><br />\r\n<br />\r\n<span style="color:#003300;">例一，不指定seed：&nbsp;</span><br />\r\n<span style="color:#003300;">for(int &nbsp;i=0;i &lt;10;i++){</span><br />\r\n<span style="color:#003300;">ran_num=rand()\r\n&nbsp;% &nbsp;6;&nbsp;</span><br />\r\n<span style="color:#003300;">cout &lt; &lt; &lt; "\r\n&nbsp;";&nbsp;</span><br />\r\n<span style="color:#003300;">}&nbsp;</span><br />\r\n<span style="color:#003300;">每次运行都将输出：5 &nbsp;5\r\n&nbsp;4 &nbsp;4 &nbsp;5\r\n&nbsp;4 &nbsp;0 &nbsp;0\r\n&nbsp;4 &nbsp;2&nbsp;</span><br />\r\n<br />\r\n<span style="color:#003300;">例二，指定seed为定值1：&nbsp;</span><br />\r\n<span style="color:#003300;">srand(1);&nbsp;</span><br />\r\n<span style="color:#003300;">for(int &nbsp;i=0;i &lt;10;i++){</span><br />\r\n<span style="color:#003300;">ran_num=rand()\r\n&nbsp;% &nbsp;6;&nbsp;</span><br />\r\n<span style="color:#003300;">cout &lt; &lt; &lt; "\r\n&nbsp;";&nbsp;</span><br />\r\n<span style="color:#003300;">}&nbsp;</span><br />\r\n<span style="color:#003300;">每次运行都将输出：5 &nbsp;5\r\n&nbsp;4 &nbsp;4 &nbsp;5\r\n&nbsp;4 &nbsp;0 &nbsp;0\r\n&nbsp;4 &nbsp;2&nbsp;</span><br />\r\n<span style="color:#003300;">跟例子一的结果完全一样。&nbsp;</span><br />\r\n<br />\r\n<span style="color:#003300;">例三，指定seed为定值6：&nbsp;</span><br />\r\n<span style="color:#003300;">srand(6);&nbsp;</span><br />\r\n<span style="color:#003300;">for(int &nbsp;i=0;i &lt;10;i++){</span><br />\r\n<span style="color:#003300;">ran_num=rand()\r\n&nbsp;% &nbsp;6;&nbsp;</span><br />\r\n<span style="color:#003300;">cout &lt; &lt; &lt; "\r\n&nbsp;";&nbsp;</span><br />\r\n<span style="color:#003300;">}&nbsp;</span><br />\r\n<span style="color:#003300;">每次运行都将输出：4 &nbsp;1\r\n&nbsp;5 &nbsp;1 &nbsp;4\r\n&nbsp;3 &nbsp;4 &nbsp;4\r\n&nbsp;2 &nbsp;2&nbsp;</span><br />\r\n<span style="color:#003300;">随机值也是在[0,6）之间，随得的值跟srand(1)不同，但是每次运行的结果都相同。&nbsp;</span><br />\r\n<br />\r\n<span style="color:#003300;">例四，指定seed为当前系统流逝了的时间（单位为秒）：time_t\r\n&nbsp;time(0)：&nbsp;</span><br />\r\n<span style="color:#003300;">#include &nbsp;&nbsp;</span><br />\r\n<span style="color:#003300;">//…&nbsp;</span><br />\r\n<span style="color:#003300;">srand((unsigned)time(0));&nbsp;</span><br />\r\n<span style="color:#003300;">for(int &nbsp;i=0;i &lt;10;i++){</span><br />\r\n<span style="color:#003300;">ran_num=rand()\r\n&nbsp;% &nbsp;6;&nbsp;</span><br />\r\n<span style="color:#003300;">cout &lt; &lt; &lt; "\r\n&nbsp;";&nbsp;</span><br />\r\n<span style="color:#003300;">}&nbsp;</span><br />\r\n<span style="color:#003300;">第一次运行时输出：0 &nbsp;1\r\n&nbsp;5 &nbsp;4 &nbsp;5\r\n&nbsp;0 &nbsp;2 &nbsp;3\r\n&nbsp;4 &nbsp;2&nbsp;</span><br />\r\n<span style="color:#003300;">第二次：3 &nbsp;2\r\n&nbsp;3 &nbsp;0 &nbsp;3\r\n&nbsp;5 &nbsp;5 &nbsp;2\r\n&nbsp;2 &nbsp;3&nbsp;</span><br />\r\n<span style="color:#003300;">总之，每次运行结果将不一样，因为每次启动程序的时刻都不同（间隔须大于1秒？见下）。&nbsp;</span><br />\r\n<br />\r\n<span style="color:#003300;">关于time_t &nbsp;time(0)：&nbsp;</span><br />\r\n<br />\r\n<span style="color:#003300;">time_t被定义为长整型，它返回从1970年1月1日零时零分零秒到目前为止所经过的时间，单位为秒。比如假设输出：</span><br />\r\n<span style="color:#003300;">cout &lt;</span><br />\r\n<span style="color:#003300;">值约为1169174701，约等于37（年）乘365（天）乘24（小时）乘3600（秒）（月日没算）。&nbsp;</span><br />\r\n<br />\r\n<span style="color:#003300;">另外，关于ran_num &nbsp;= &nbsp;rand() &nbsp;% &nbsp;6，&nbsp;</span><br />\r\n<br />\r\n<span style="color:#003300;">将rand()的返回值与6求模是必须的，这样才能确保目的随机数落在[0,6)之间，否则rand()的返回值本身可能是很巨大的。&nbsp;</span><br />\r\n<span style="color:#003300;">一个通用的公式是：&nbsp;</span><br />\r\n<span style="color:#003300;">要取得[a,b)之间的随机整数，使用（rand()\r\n&nbsp;% &nbsp;(b-a)）+ &nbsp;a &nbsp;（结果值将含a不含b）。&nbsp;</span><br />\r\n<span style="color:#003300;">在a为0的情况下，简写为rand()\r\n&nbsp;% &nbsp;b。&nbsp;</span><br />\r\n<br />\r\n<span style="color:#003300;">最后，关于伪随机浮点数：&nbsp;</span><br />\r\n<br />\r\n<span style="color:#003300;">用rand() &nbsp;/\r\n&nbsp;double(RAND_MAX)可以取得0～1之间的浮点数（注意，不同于整型时候的公式，是除以，不是求模），举例：&nbsp;</span><br />\r\n<span style="color:#003300;">double &nbsp;ran_numf=0.0;&nbsp;</span><br />\r\n<span style="color:#003300;">srand((unsigned)time(0));&nbsp;</span><br />\r\n<span style="color:#003300;">for(int &nbsp;i=0;i &lt;10;i++){</span><br />\r\n<span style="color:#003300;">ran_numf &nbsp;=\r\n&nbsp;rand() &nbsp;/ &nbsp;(double)(RAND_MAX);&nbsp;</span><br />\r\n<span style="color:#003300;">cout &lt; &lt; &lt; "\r\n&nbsp;";&nbsp;</span><br />\r\n<span style="color:#003300;">}&nbsp;</span><br />\r\n<span style="color:#003300;">运行结果为：0.716636，0.457725，…等10个0～1之间的浮点数，每次结果都不同。&nbsp;</span><br />\r\n<br />\r\n<span style="color:#003300;">如果想取更大范围的随机浮点数，比如1～10，可以将&nbsp;</span><br />\r\n<span style="color:#003300;">rand() &nbsp;/(double)(RAND_MAX) &nbsp;改为 &nbsp;rand()\r\n&nbsp;/(double)(RAND_MAX/10)&nbsp;</span><br />\r\n<span style="color:#003300;">运行结果为：7.19362，6.45775，…等10个1～10之间的浮点数，每次结果都不同。&nbsp;</span><br />\r\n<span style="color:#003300;">至于100，1000的情况，如此类推。&nbsp;</span><br />\r\n<br />\r\n<span style="color:#003300;">以上不是伪随机浮点数最好的实现方法，不过可以将就着用用… <br />\r\n</span>\r\n</p>', 1385525704, 1385525704, 84, '未知', 50, 'rand() 和 srand（）', 1, 1, 1),
(25, '', '', 5, '', ' 联合(union)在C/C++里面见得并不多，但是在一些对内存要求特别严格的地方，联合又是频繁出现，那么究竟什么是联合、怎么去用、有什么需要注意的地方呢？就这些问题，我试着做一些简单的回答，里面肯定还有不当的地方，欢迎指出！ 1、什么是联合？ “联合”是一种特殊的类，也是一种构造类型的数据结构。在一个“联合”内可以定义多种不同的数据类型， 一个被说明为该“联', '<p style="color:#444444;font-family:Verdana, Helvetica, Arial, sans-serif;font-size:14px;background-color:#FFFFFF;">\r\n	联合(union)在C/C++里面见得并不多，但是在一些对内存要求特别严格的地方，联合又是频繁出现，那么究竟什么是联合、怎么去用、有什么需要注意的地方呢？就这些问题，我试着做一些简单的回答，里面肯定还有不当的地方，欢迎指出！\r\n</p>\r\n<p style="color:#444444;font-family:Verdana, Helvetica, Arial, sans-serif;font-size:14px;background-color:#FFFFFF;">\r\n	<br />\r\n<strong>1、什么是联合？</strong><br />\r\n“联合”是一种特殊的类，也是一种构造类型的数据结构。在一个“联合”内可以定义多种不同的数据类型， 一个被说明为该“联合”类型的变量中，允许装入该“联合”所定义的任何一种数据，这些数据共享同一段内存，已达到节省空间的目的（还有一个节省空间的类型：位域）。 这是一个非常特殊的地方，也是联合的特征。另外，同struct一样，联合默认访问权限也是公有的，并且，也具有成员函数。\r\n</p>\r\n<p style="color:#444444;font-family:Verdana, Helvetica, Arial, sans-serif;font-size:14px;background-color:#FFFFFF;">\r\n	<strong><br />\r\n2、联合与结构的区别？</strong><br />\r\n“联合”与“结构”有一些相似之处。但两者有本质上的不同。在结构中各成员有各自的内存空间， 一个结构变量的总长度是各成员长度之和（空结构除外，同时不考虑边界调整）。而在“联合”中，各成员共享一段内存空间， 一个联合变量的长度等于各成员中最长的长度。应该说明的是， 这里所谓的共享不是指把多个成员同时装入一个联合变量内， 而是指该联合变量可被赋予任一成员值，但每次只能赋一种值， 赋入新值则冲去旧值。<br />\r\n下面举一个例了来加对深联合的理解。\r\n</p>\r\n<p style="color:#444444;font-family:Verdana, Helvetica, Arial, sans-serif;font-size:14px;background-color:#FFFFFF;margin-left:40px;">\r\n	<br />\r\n例4:&nbsp;<br />\r\n#include &lt;stdio.h&gt;<br />\r\nvoid main()<br />\r\n{<br />\r\nunion number<br />\r\n{ /*定义一个联合*/<br />\r\nint i;<br />\r\nstruct<br />\r\n{ /*在联合中定义一个结构*/<br />\r\nchar first;<br />\r\nchar second;<br />\r\n}half;<br />\r\n}num;<br />\r\nnum.i=0x4241; /*联合成员赋值*/<br />\r\nprintf("%c%c\\n", num.half.first, num.half.second);<br />\r\nnum.half.first=''a''; /*联合中结构成员赋值*/<br />\r\nnum.half.second=''b'';<br />\r\nprintf("%x\\n", num.i);<br />\r\ngetchar();<br />\r\n}<br />\r\n输出结果为:&nbsp;<br />\r\nAB&nbsp;<br />\r\n6261&nbsp;<br />\r\n&nbsp;\r\n</p>\r\n<p style="color:#444444;font-family:Verdana, Helvetica, Arial, sans-serif;font-size:14px;background-color:#FFFFFF;">\r\n	从上例结果可以看出: 当给i赋值后, 其低八位也就是first和second的值; 当给first和second赋字符后, 这两个字符的ASCII码也将作为i 的低八位和高八位。<br />\r\n&nbsp;\r\n</p>\r\n<p style="color:#444444;font-family:Verdana, Helvetica, Arial, sans-serif;font-size:14px;background-color:#FFFFFF;">\r\n	3、如何定义？\r\n</p>\r\n<p style="color:#444444;font-family:Verdana, Helvetica, Arial, sans-serif;font-size:14px;background-color:#FFFFFF;margin-left:40px;">\r\n	<br />\r\n例如：<br />\r\nunion test<br />\r\n{<br />\r\ntest() { }<br />\r\nint office;<br />\r\nchar teacher[5];<br />\r\n};\r\n</p>\r\n<p style="color:#444444;font-family:Verdana, Helvetica, Arial, sans-serif;font-size:14px;background-color:#FFFFFF;">\r\n	<br />\r\n定义了一个名为test的联合类型，它含有两个成员，一个为整型，成员名office；另一个为字符数组，数组名为teacher。联合定义之后，即可进行联合变量说明，被说明为test类型的变量，可以存放整型量office或存放字符数组teacher。\r\n</p>\r\n<p style="color:#444444;font-family:Verdana, Helvetica, Arial, sans-serif;font-size:14px;background-color:#FFFFFF;">\r\n	<br />\r\n4、如何说明？<br />\r\n联合变量的说明有三种形式：先定义再说明、定义同时说明和直接说明。\r\n</p>\r\n<p style="color:#444444;font-family:Verdana, Helvetica, Arial, sans-serif;font-size:14px;background-color:#FFFFFF;margin-left:40px;">\r\n	<br />\r\n以test类型为例，说明如下：<br />\r\n1) union test<br />\r\n{<br />\r\nint office;<br />\r\nchar teacher[5];<br />\r\n};&nbsp;<br />\r\nunion test a,b; /*说明a,b为test类型*/<br />\r\n2) union test<br />\r\n{<br />\r\nint office;<br />\r\nchar teacher[5];<br />\r\n} a,b;<br />\r\n3) union&nbsp;<br />\r\n{<br />\r\nint office;<br />\r\nchar teacher[5];<br />\r\n} a,b;\r\n</p>\r\n<p style="color:#444444;font-family:Verdana, Helvetica, Arial, sans-serif;font-size:14px;background-color:#FFFFFF;">\r\n	<br />\r\n经说明后的a,b变量均为test类型。a,b变量的长度应等于test的成员中最长的长度，即等于teacher数组的长度，共5个字节。a,b变量如赋予整型值时，只使用了4个字节，而赋予字符数组时，可用5个字节。\r\n</p>', 1386172549, 1386172549, 59, '未知', 50, 'c++ union', 1, 1, 1),
