  `is_check` tinyint(1) NOT NULL DEFAULT '0' COMMENT '是否通过',
  `adminid` int(11) NOT NULL DEFAULT '1',
  `status` int(1) DEFAULT '1',
  `source` varchar(200) NOT NULL DEFAULT '0',
  `source_link` varchar(200) NOT NULL DEFAULT '0',
  PRIMARY KEY (`id`),
  KEY `arcindex` (`cid`)
